/**
 * Begin playback
 */
export declare function play(): {
    address: string;
};
/**
* Stop playback
*/
export declare function stop(): {
    address: string;
};
/**
 * Set loop points, and enable looping.
 * @param startTimeInWholeNotes The number of whole notes at which the loop
 *    should begin. Or `false`, which disables looping.
 * @param durationInWholeNotes The length of the loop, measured in whole notes.
 */
export declare function loop(startTimeInWholeNotes: number | boolean, durationInWholeNotes?: number): {
    address: string;
    args: {
        type: string;
        value: number;
    }[];
};
/**
 * Move the playback transport
 */
export declare function to(timeInWholeNotes: any): {
    address: string;
    args: {
        type: string;
        value: number;
    }[];
};
